pragma solidity ^0.5.0;

contract Users {
  // Store devices
  // Create mapping between users and devices

  // Add device to mapping
  mapping(string => string) public usernamesToDeviceIds;

  // Store users count
  uint public usersCount;

  // Store device counts per user
  mapping(string => uint) public usernamesToDeviceCounts;

  // Get devices by user id

  constructor() public {}

  function addDeviceToUsername(string memory _deviceId, string memory _username) public {
    uint prevDeviceCount = usernamesToDeviceCounts[_username];
    usernamesToDeviceCounts[_username] = prevDeviceCount + 1;
    usernamesToDeviceIds[_username] = _deviceId;
  }
}
